import React from 'react';
import { Redirect, BrowserRouter, withRouter } from "react-router-dom";
import './menu.scss';

interface IProps {}
interface IState {
    activeItem: string;
    activeItemPosition: number,
    activeItemColor: string,
    menuItems: Array<{ text: string}>,
    redirect: boolean,
}

class MenuContainer extends React.PureComponent<IProps, IState> {
    constructor(props: IProps) {
      super(props)

      this.state = {
        activeItem: '',
        activeItemPosition: 0,
        activeItemColor: '',
        menuItems: [
            { text: 'Home' },
            { text: 'Hypertension' },
            { text: 'Kidney Disease' },
            { text: 'About' },
            { text: 'Contact' },
        ],
        redirect: false,
      }

      this.handleClick = this.handleClick.bind(this)
    }

    componentDidUpdate() {
        this.setState({ redirect: false });
    }

    handleClick(activeItem: any) {
      return (e: any) => {
        e.preventDefault()      
        this.setState({
          activeItem,
          activeItemPosition: e.target.offsetTop,
          activeItemColor: window.getComputedStyle(e.target).getPropertyValue('background-color'),
          redirect: true,
        })
      }
    }

    render() {
        if (this.state.redirect) {
            return <Redirect push to={this.state.activeItem.replace(' ', '')} />;
        }
        const menuItems = this.state.menuItems.map(item => <MenuItem item={ item } handleClick={ this.handleClick } key={item.text}/>)
        return (
            <React.Fragment>
                <div className='menu-container'>
                <span className='menu-item--active' style={{ top: this.state.activeItemPosition, backgroundColor: this.state.activeItemColor }}/>
                { menuItems }
                </div>
            </React.Fragment>
        )
    }
  }


  function MenuItem(props: any) {
    return (
        <div 
            className='menu-item'
            id={ props.item.text }
            onClick={ props.handleClick(props.item.text) }
        >
            { props.item.text.toUpperCase() }
        </div>
    )
  }


  export default MenuContainer;
