import React from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import './menu.scss';
import Hypertension from '../hypertension/Hypertension';
import KidneyDisease from '../kidneyDisease/KidneyDisease';

interface IProps {}
interface IState {
    activeItem: string;
    activeItemPosition: number,
    activeItemColor: string,
    menuItems: Array<{}>,
}

function Index() {
    return <h2>Home</h2>;
}

class MenuContainer extends React.PureComponent<IProps, IState> {
    constructor(props: IProps) {
      super(props)

      this.state = {
        activeItem: '',
        activeItemPosition: 0,
        activeItemColor: '',
        menuItems: [
          { text: 'Hypertension' },
          { text: 'Kidney Disease' },
        ],
      }

      this.handleClick = this.handleClick.bind(this)
    }

    handleClick(activeItem: any) {
      return (e: any) => {
        e.preventDefault()      
        this.setState({
          activeItem,
          activeItemPosition: e.target.offsetTop,
          activeItemColor: window.getComputedStyle(e.target).getPropertyValue('background-color'),
        })
      }
    }

    render() {
      const menuItems = this.state.menuItems.map(item => <MenuItem item={ item } handleClick={ this.handleClick }/>)
      return (
        <div className='menu-container'>
          <span className='menu-item--active' style={{ top: this.state.activeItemPosition, backgroundColor: this.state.activeItemColor }}/>
          { menuItems }
        </div>
      )
    }
  }


  function MenuItem(props: any) {
    return (
        <div 
            className='menu-item'
            id={ props.item.text }
            onClick={ props.handleClick(props.item.text) }
        >
            { props.item.text.toUpperCase() }
        </div>
    )
  }


  export default MenuContainer;
