import React from 'react';
import './menu.scss';

    interface IProps {}
    interface IState {
        activeItem: string;
        activeItemPosition: number,
        activeItemColor: string,
        menuItems: Array<{}>,
    }

class MenuContainer extends React.PureComponent<IProps, IState> {
    constructor(props: IProps) {
      super(props)

      this.state = {
        activeItem: '',
        activeItemPosition: 0,
        activeItemColor: '',
        menuItems: [
          { text: 'Home' },
          { text: 'Gallery' },
          { text: 'About' },
          { text: 'Blog' },
          { text: 'Contact' },
        ],
      }

      this.handleClick = this.handleClick.bind(this)
    }

    handleClick(activeItem: any) {
      return (e: any) => {
        e.preventDefault()

        console.log(e.target)

        // this.setState({
        //   activeItem,
        //   activeItemPosition: document.getElementById(activeItem).offsetTop,
        //   activeItemColor: window.getComputedStyle(document.getElementById(activeItem)).getPropertyValue('background-color'),
        // })
      }
    }

    render() {
      const menuItems = this.state.menuItems.map(item => <MenuItem item={ item } handleClick={ this.handleClick }/>)
      return (
        <div className='menu-container'>
          <span className='menu-item--active' style={{ top: this.state.activeItemPosition, backgroundColor: this.state.activeItemColor }}/>
          { menuItems }
        </div>
      )
    }
  }


  function MenuItem(props: any) {
    return (
      <div 
        className='menu-item'
        id={ props.item.text }
        onClick={ props.handleClick(props.item.text) }
      >
        { props.item.text.toUpperCase() }
      </div>
    )
  }


  export default MenuContainer;
